mapper.select:
SELECT



FROM fangdong  a



        and a.id in

        #{item}



        and a.username like CONCAT('%',#{params.username},'%')


        and a.password like CONCAT('%',#{params.password},'%')


        and a.fangdong_name like CONCAT('%',#{params.fangdongName},'%')


        and a.fangdong_phone like CONCAT('%',#{params.fangdongPhone},'%')


        and a.fangdong_id_number like CONCAT('%',#{params.fangdongIdNumber},'%')


        and a.sex_types = #{params.sexTypes}


        and a.fangdong_email like CONCAT('%',#{params.fangdongEmail},'%')


          and a.new_money >= #{params.newMoneyStart}   


          and a.new_money <= #{params.newMoneyEnd}   



order by a.${params.sort} ${params.order}
