mapper.select:
SELECT


,fangwu.fangdong_id as fangwuFangdongId
,fangwu.fangwu_name as fangwuName
,fangwu.fangwu_uuid_number as fangwuUuidNumber
,fangwu.fangwu_photo as fangwuPhoto
,fangwu.fangwu_address as fangwuAddress
,fangwu.fangwu_types as fangwuTypes
,fangwu.zan_number as zanNumber
,fangwu.cai_number as caiNumber
,fangwu.fangwu_new_money as fangwuNewMoney
,fangwu.fangwu_content as fangwuContent
,fangwu.fangwu_delete as fangwuDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.new_money as newMoney

FROM fangwu_commentback  a
left JOIN fangwu fangwu ON a.fangwu_id = fangwu.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.fangwu_id = #{params.fangwuId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.fangwu_commentback_text like CONCAT('%',#{params.fangwuCommentbackText},'%')


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.reply_text like CONCAT('%',#{params.replyText},'%')


          and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


          and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 




        and a.fangwu_id IS NOT NULL


        and fangwu.fangdong_id = #{params.fangdongId}


        and fangwu.fangwu_name like CONCAT('%',#{params.fangwuName},'%')


        and fangwu.fangwu_uuid_number like CONCAT('%',#{params.fangwuUuidNumber},'%')


        and fangwu.fangwu_address like CONCAT('%',#{params.fangwuAddress},'%')


        and fangwu.fangwu_types = #{params.fangwuTypes}



          and fangwu.zan_number >= #{params.zanNumberStart}   


          and fangwu.zan_number <= #{params.zanNumberEnd}   


        and fangwu.zan_number = #{params.zanNumber}


          and fangwu.cai_number >= #{params.caiNumberStart}   


          and fangwu.cai_number <= #{params.caiNumberEnd}   


        and fangwu.cai_number = #{params.caiNumber}


          and fangwu.fangwu_new_money >= #{params.fangwuNewMoneyStart}   


          and fangwu.fangwu_new_money <= #{params.fangwuNewMoneyEnd}   


        and fangwu.fangwu_content like CONCAT('%',#{params.fangwuContent},'%')


          and fangwu.fangwu_delete >= #{params.fangwuDeleteStart}   


          and fangwu.fangwu_delete <= #{params.fangwuDeleteEnd}   


        and fangwu.fangwu_delete = #{params.fangwuDelete}


          and UNIX_TIMESTAMP(fangwu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(fangwu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.new_money >= #{params.newMoneyStart}   


          and yonghu.new_money <= #{params.newMoneyEnd}   


order by a.${params.sort} ${params.order}
